import React, { useState } from 'react'
import ListOfMovies from '../ListOfMovies';
export default function AddMovie() {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [posterURL, setPosterURL] = useState("")
    const [rating, setRating] = useState(0);

const handleSubmit = (e) =>{
    e.preventDefault();
    ListOfMovies.push({
        title : {title},
        description : {description},
        posterURL : {posterURL},
        rating : {rating}
    })
}

  return (
      <div style={{paddingLeft:'27%'}} class="container-fluid mt-4 fw-bold">
    <form class="m-2 col-12 ">
       <div class="col-8 mb-3">
         <label for="title" class="form-label">TITLE</label>
         <input type="text" class="form-control" id="title" placeholder="title" onChange={e => setTitle(e.target.value)}/>
       </div>
      <div class=" col-8 mb-3">
         <label for="description" class="form-label">DESCRIPTION</label>
         <textarea class="form-control" id="description" rows="3" onChange={e => setDescription(e.target.value)}></textarea>
      </div>
      <div class="col-8 mb-3">
         <label for="posterURL" class="form-label">IMAGE</label>
         <input type="text" class="form-control" id="posterURL" placeholder="poster url" onChange={e => setPosterURL(e.target.value)}/>
       </div>
       <div class="col-8 mb-3">
         <label for="rating" class="form-label">RATING</label>
         <input type="number" class="form-control" id="rating" onChange={e => setRating(e.target.value)}/>
       </div>
    </form>
    <button class="btn btn-outline-primary fw-bold" id="addMovie">SUBMIT</button>
    </div>
  )
}
